import React, { useState } from 'react';
import { Btn, Container } from '../styles/App'
import { GlobalStyle } from '../styles/Global'
import MetaData from './MetaData';

const cardImgs = [
  { "src": "/img/helmet-1.png" },
  { "src": "/img/potion-1.png" },
  { "src": "/img/ring-1.png" },
  { "src": "/img/scroll-1.png" },
  { "src": "/img/shield-1.png" },
  { "src": "/img/sword-1.png" }
]

function App() {

  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)

  const shuffleCards = () => {
    //! duplicate images
    const shuffledCards = [...cardImgs, ...cardImgs]
      .sort(() => Math.random() - 0.5) //! shuffle cards (less than 0, order stays the same, bigger than 0 order changes)
      .map((card) => ({...card, id: Math.random})) //! add random id

      setCards(shuffledCards)
      setTurns(0)
  }
  console.log(cards, turns)

  return (
    <>
    <MetaData />
    <GlobalStyle /> 
    <Container>
      <h1>Magic Match</h1>
      <Btn onClick={shuffleCards}>New Game</Btn>

      <div className="card-grid">
        {cards.map((card) => (
            <div className='card' key={card.id}>
              <div>
                <img src={card.src} alt="card back" className="front" />
                <img src="/img/cover.png" alt="card back" className="back" />
              </div>
            </div>

        ))}
      </div>
    </Container>
    </>
  );
}

export default App
