import React from 'react'
import { Flipped, SingleCard } from '../styles/Cards'

function Card({card, handleChoice, flipped}) {

    const handleClick = () => {
        handleChoice(card)
    }

    return (
        <SingleCard key={card.id}>
            <Flipped className={flipped ? "flipped" : ""}>
                <img src={card.src} alt="card back" className="front" />
                <img src="/img/cover.png" alt="card back" className="back" onClick={handleClick}/>
            </Flipped>
        </SingleCard>
    )
}

export default Card
